import { useState } from "react";
import { Container, Navbar, Nav, Row, Col, Card, Button } from "react-bootstrap";
import { FaMoon, FaSun, FaPhone, FaUser } from "react-icons/fa";
import "bootstrap/dist/css/bootstrap.min.css";

export default function HomePage() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleLogin = () => {
    alert("Redirecionando para a página de login...");
    // Se estiver usando React Router:
    // navigate("/login");
  };

  const themeClass = darkMode ? "bg-dark text-light" : "bg-light text-dark";

  return (
    <div className={`w-100 min-vh-100 d-flex flex-column ${themeClass}`}>
  {/* Navbar */}
  <Navbar expand="lg" className={`px-4 ${darkMode ? "bg-secondary" : "bg-primary"}`} variant={darkMode ? "dark" : "light"}>
    <Navbar.Brand href="#" className="fw-bold text-white">
      Curso<span className="text-warning">Finder</span>
    </Navbar.Brand>
    <Nav className="ms-auto align-items-center gap-3">
      <Nav.Link href="#home" className="text-white">Home</Nav.Link>
      <Nav.Link href="#categorias" className="text-white">Categorias</Nav.Link>
      <FaPhone className="text-white" title="Fale Conosco" style={{ cursor: "pointer" }} />
      <Button variant="light" size="sm" onClick={handleLogin}>
        <FaUser className="me-2" /> Login
      </Button>
      <Button variant={darkMode ? "light" : "dark"} size="sm" onClick={toggleDarkMode}>
        {darkMode ? <FaSun /> : <FaMoon />}
      </Button>
    </Nav>
  </Navbar>

  {/* Header */}
  <header className="text-center py-5 bg-light flex-grow-1 d-flex flex-column justify-content-center align-items-center">
    <h2 className="fw-bold">
      Menos <span className="text-primary">busca</span>, mais <span className="text-success">aprendizado</span>
    </h2>
    <p className="lead">Encontre os melhores cursos em poucos cliques.</p>
    <Button variant="success">Ver Cursos &rarr;</Button>
  </header>

  {/* Footer */}
  <footer className="bg-dark text-white py-4 w-100 mt-auto">
    <Container className="text-center">
      <hr className="my-3" />
      <p className="mb-0">&copy; 2025 CursoFinder - Todos os direitos reservados</p>
    </Container>
  </footer>
</div>

  );
}
