import React, { useState, useEffect } from "react";
import {
  Container, Navbar, Nav, Row, Col, Card, Button, Form
} from "react-bootstrap";
import { FaMoon, FaSun, FaUser, FaSignOutAlt } from "react-icons/fa";
import { useNavigate, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import jwtDecode from "jwt-decode";  // ajustei a importação para a forma correta
import axios from "axios";

export default function SearchCoursesPage() {
  const [darkMode, setDarkMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [userName, setUserName] = useState(null);
  const navigate = useNavigate();

  // Busca cursos da API
  useEffect(() => {
    async function fetchCourses() {
      try {
        const response = await axios.get("https://localhost:7238/api/Curso");
        setCourses(response.data);
        setFilteredCourses(response.data);
      } catch (error) {
        console.error("Erro ao buscar cursos:", error);
      }
    }
    fetchCourses();
  }, []);

  // Checa token e usuário
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      try {
        const decoded = jwtDecode(token);
        setUserName(decoded?.userName || decoded?.unique_name || "Usuário");
      } catch (error) {
        console.error("Token inválido");
        setUserName(null);
      }
    } else {
      setUserName(null);
    }
  }, []);

  const mainTheme = darkMode ? "bg-dark text-light" : "bg-light text-dark";
  const variant = darkMode ? "dark" : "light";
  const bgClass = darkMode ? "bg-secondary" : "bg-primary";
  const toggleDarkMode = () => setDarkMode(!darkMode);

  // Função de busca local no array de cursos já carregado
  const handleSearch = () => {
    if (!searchTerm) {
      setFilteredCourses(courses);
      return;
    }
    const filtered = courses.filter(course =>
      course.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCourses(filtered);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setUserName(null);
    navigate("/"); // redireciona para home após logout
  };

  return (
    <div className={mainTheme} style={{ minHeight: "100vh" }}>
      <Navbar expand="lg" variant={variant} className={`${bgClass} py-3`} fixed="top">
        <Container>
          <Navbar.Brand as={Link} to="/" className="fw-bold text-white">
            Curso<span className="text-warning">Finder</span>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="navbar-nav" />
          <Navbar.Collapse id="navbar-nav">
            <Nav className="ms-auto align-items-center gap-3">
              <Nav.Link as={Link} to="/" className="text-white">
                Home
              </Nav.Link>
              <Nav.Link as={Link} to="/meuscursos" className="text-white">
                Meus Cursos Salvos
              </Nav.Link>

              {userName ? (
                <>
                  <span className="text-white fw-semibold">{userName}</span>
                  <Button
                    variant="light"
                    size="sm"
                    onClick={handleLogout}
                    className="d-flex align-items-center gap-1"
                    title="Logout"
                  >
                    <FaSignOutAlt />
                    Sair
                  </Button>
                </>
              ) : (
                <Button variant="light" size="sm" onClick={() => navigate("/login")}>
                  <FaUser className="me-2" />
                  Login
                </Button>
              )}

              <Button
                variant={darkMode ? "light" : "dark"}
                size="sm"
                onClick={toggleDarkMode}
              >
                {darkMode ? <FaSun /> : <FaMoon />}
              </Button>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <div style={{ paddingTop: '75px', paddingBottom: '150px' }}>
        <Container className="py-5">
          <h2 className="text-center mb-4">Busque o curso dos seus sonhos!</h2>

          <Form.Group controlId="search" className="mb-4">
            <Form.Control
              type="text"
              placeholder="Digite o nome do curso"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              onKeyDown={(e) => { if (e.key === "Enter") { e.preventDefault(); handleSearch(); } }}
              style={{ height: '60px', fontSize: '1.25rem' }}
            />
            <div className="text-center mt-3">
              <Button variant="primary" size="lg" onClick={handleSearch}>
                Buscar
              </Button>
            </div>
          </Form.Group>

          <Row className="g-4">
            {filteredCourses.length > 0 ? (
              filteredCourses.map(course => (
                <Col key={course.id} md={4}>
                  <Card className="h-100 shadow">
                    <Card.Body>
                      <Card.Title>{course.title}</Card.Title>
                      <Card.Text>{course.description}</Card.Text>
                      <Button variant="secondary" disabled>
                        Saiba mais
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              ))
            ) : (
              <Col className="text-center">
                <p>Nenhum curso encontrado. Tente outro nome.</p>
              </Col>
            )}
          </Row>
        </Container>
      </div>

      <footer className="bg-dark text-white py-4 mb-0" style={{ marginTop: "150px" }}>
        <Container className="text-center">
          <hr className="my-3" />
          <p className="mb-0">&copy; 2025 CursoFinder - Todos os direitos reservados</p>
        </Container>
      </footer>
    </div>
  );
}
