import React, { createContext, useContext, useState, useEffect } from "react";
import { jwtDecode } from "jwt-decode"; // Atenção: use a forma correta de importar conforme sua versão!

const AuthContext = createContext();

export function AuthProvider({ children }) {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);

  // Função para logar: salva token, decodifica e seta user
  const login = (jwtToken) => {
    setToken(jwtToken);
    localStorage.setItem("token", jwtToken);

    try {
      const decoded = jwtDecode(jwtToken);
      setUser(decoded);
    } catch (error) {
      console.error("Erro ao decodificar token:", error);
      setUser(null);
    }
  };

  // Função para deslogar: limpa tudo
  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem("token");
  };

  // Ao montar, tenta recuperar o token do localStorage
  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken) {
      login(storedToken);
    }
  }, []);

  return (
    <AuthContext.Provider value={{ user, token, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

// Hook para usar o contexto de forma simples
export function useAuth() {
  return useContext(AuthContext);
}
