import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';

const AdminFaculdade = () => {
  const [cursos, setCursos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [mensagem, setMensagem] = useState('');

  const token = localStorage.getItem('token');

  const fetchCursos = async () => {
    try {
      const response = await axios.get('https://localhost:7227/api/Curso/meus-cursos', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setCursos(response.data);
    } catch (error) {
      setMensagem('Erro ao buscar cursos.');
    } finally {
      setLoading(false);
    }
  };

  const excluirCurso = async (id) => {
    try {
      await axios.delete(`https://localhost:7227/api/Curso/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMensagem('Curso excluído com sucesso.');
      fetchCursos();
    } catch (error) {
      setMensagem('Erro ao excluir curso.');
    }
  };

  useEffect(() => {
    fetchCursos();
  }, []);

  if (loading) return <p>Carregando...</p>;

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Gerenciar Cursos da Minha Faculdade</h2>
      {mensagem && <p className="mb-4 text-green-600">{mensagem}</p>}

      {cursos.length === 0 ? (
        <p>Nenhum curso cadastrado ainda.</p>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {cursos.map((curso) => (
            <Card key={curso.id} className="shadow-lg">
              <CardContent>
                <h3 className="font-semibold text-lg">{curso.nome}</h3>
                <p><strong>Área:</strong> {curso.area}</p>
                <p><strong>Duração:</strong> {curso.duracao}</p>
                <div className="mt-3 flex gap-2">
                  <Button onClick={() => excluirCurso(curso.id)} variant="destructive">Excluir</Button>
                  {/* Você pode adicionar um botão de edição aqui */}
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default AdminFaculdade;
