import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Login() {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await fetch("https://localhost:7238/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, password: senha }), // Certifique-se de que o backend espera "password"
      });

      if (!response.ok) {
        const errorText = await response.text(); // Lê a resposta do erro como texto
        throw new Error(errorText || "Erro desconhecido no servidor");
      }

      const data = await response.json(); // Converte a resposta em JSON apenas se for um sucesso
      localStorage.setItem("token", data.token);
      navigate("/dashboard"); // Redireciona após login
    } catch (error) {
      console.error("Erro ao fazer login:", error.message);
      alert(`Verifique se seu e-mail ou senha são válidos`);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h2>Login</h2>
        <label>E-mail</label>
        <input
          type="email"
          placeholder="Digite seu e-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label>Senha</label>
        <input
          type="password"
          placeholder="Digite sua senha"
          value={senha}
          onChange={(e) => setSenha(e.target.value)}
        />
        <div className="links">
          <a href="/recuperacao-senha">Esqueceu a senha?</a>
          <a href="/cadastro">Cadastrar-se</a>
        </div>
        <button onClick={handleLogin}>Login</button>
      </div>
    </div>
  );
}
