import { useState } from "react";
import {
    Container,
    Navbar,
    Nav,
    Row,
    Col,
    Card,
    Button,
} from "react-bootstrap";
import { FaMoon, FaSun, FaPhone, FaUser } from "react-icons/fa";
import "bootstrap/dist/css/bootstrap.min.css";

export default function HomePage() {
    const [darkMode, setDarkMode] = useState(false);

    const toggleDarkMode = () => setDarkMode(!darkMode);
    const handleLogin = () => {
        alert("Redirecionando para a página de login...");
        // Exemplo: navegação com React Router: navigate("/login");
    };

    const mainTheme = darkMode ? "bg-dark text-light" : "bg-light text-dark";
    const navTheme = darkMode ? "dark" : "light";
    const navBg = darkMode ? "bg-secondary" : "bg-primary";

    return (
        <div className={mainTheme} style={{ minHeight: "100vh" }}>
            {/* Navbar */}
            <Navbar
                expand="lg"
                variant={navTheme}
                className={`${navBg} py-3`}
                fixed="top"
            >
                <Container >
                    <Navbar.Brand href="#" className="fw-bold text-white">
                        Curso<span className="text-warning">Finder</span>
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls="basic-navbar-nav" />
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="ms-auto align-items-center gap-3">
                            <Nav.Link href="#home" className="text-white">
                                Home
                            </Nav.Link>
                            <Nav.Link href="#categorias" className="text-white">
                                Categorias
                            </Nav.Link>
                            <FaPhone className="text-white" title="Fale Conosco" />
                            <Button variant="light" size="sm" onClick={handleLogin}>
                                <FaUser className="me-2" />
                                Login
                            </Button>
                            <Button
                                variant={darkMode ? "light" : "dark"}
                                size="sm"
                                onClick={toggleDarkMode}
                            >
                                {darkMode ? <FaSun /> : <FaMoon />}
                            </Button>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

            {/* Espaço para compensar a navbar fixa */}
            <div style={{ paddingTop: "90px" }}>
                {/* Header */}
                <Container className="text-center py-5">
                    <h2 className="fw-bold">
                        Menos <span className="text-primary">busca</span>, mais{" "}
                        <span className="text-success">aprendizado</span>
                    </h2>
                    <p className="lead">Encontre os melhores cursos em poucos cliques.</p>
                    <Button variant="success">Ver Cursos &rarr;</Button>
                </Container>

                {/* Cursos em Destaque */}
                <Container className="py-5">
                    <h3 className="text-center mb-4">Cursos em Destaque</h3>
                    <Row className="g-4">
                        {[1, 2, 3].map((id) => (
                            <Col key={id} md={4}>
                                <Card className="h-100 shadow">
                                    <Card.Img
                                        variant="top"
                                        src={`https://via.placeholder.com/300x150?text=Curso+${id}`}
                                    />
                                    <Card.Body>
                                        <Card.Title>Curso {id}</Card.Title>
                                        <Card.Text>
                                            Aprenda tudo sobre o Curso {id} com os melhores
                                            professores.
                                        </Card.Text>
                                        <Button variant="primary">Saiba mais</Button>
                                    </Card.Body>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                </Container>
            </div>

            {/* Footer */}
            <footer className="bg-dark text-white py-4 mt-auto">
                <Container className="text-center">
                    <hr className="my-3" />
                    <p className="mb-0">
                        &copy; 2025 CursoFinder - Todos os direitos reservados
                    </p>
                </Container>
            </footer>
        </div>
    );
}
