import { useState } from "react";
import { useSearchParams } from "react-router-dom";

export default function RedefinirSenha() {
  const [searchParams] = useSearchParams();
  const email = searchParams.get("email");
  const token = searchParams.get("token");

  const [novaSenha, setNovaSenha] = useState("");
  const [mensagem, setMensagem] = useState("");

  const handleRedefinirSenha = async () => {
    try {
      const response = await fetch("https://localhost:7238/api/auth/reset-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, token, newPassword: novaSenha }),
      });

      const data = await response.text();
      setMensagem(data);
    } catch (error) {
      setMensagem("Erro ao redefinir senha.");
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h2>Redefinir Senha</h2>
        <label>Nova Senha</label>
        <input
          type="password"
          placeholder="Digite a nova senha"
          value={novaSenha}
          onChange={(e) => setNovaSenha(e.target.value)}
        />
        <button onClick={handleRedefinirSenha}>Redefinir</button>
        {mensagem && <p>{mensagem}</p>}
      </div>
    </div>
  );
}
