import { useState } from "react";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";


export default function Cadastro() {
  const [nome, setNome] = useState("");
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [erro, setErro] = useState(""); // Estado para exibir mensagens de erro
  const navigate = useNavigate();

  const handleCadastro = async () => {
    if (!nome || !email || !senha) {
      setErro("Todos os campos são obrigatórios.");
      return;
    }

    try {
      const response = await fetch("https://localhost:7238/api/auth/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          userName: nome,
          email: email,
          password: senha,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        let errorMessage = "Erro ao cadastrar usuário.";

        if (data.errors) {
          if (data.errors.Password) {
            errorMessage = "A senha deve ter pelo menos 8 caracteres, incluindo letras maiúsculas, minúsculas e números.";
          } else if (data.errors.Email) {
            errorMessage = "Formato de e-mail inválido.";
          } else if (data.errors.UserName) {
            errorMessage = "Nome de usuário inválido. Use apenas letras e números.";
          }
        } else if (data.message) {
          errorMessage = data.message;
        }

        throw new Error(errorMessage);
      }

      localStorage.setItem("token", data.token);
      navigate("/dashboard");
    } catch (error) {
      console.error("Erro ao cadastrar:", error);
      setErro(error.message);
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h2>Cadastrar</h2>
        <label>Nome</label>
        <input
          type="text"
          placeholder="Digite seu nome"
          value={nome}
          onChange={(e) => setNome(e.target.value)}
        />
        <label>E-mail</label>
        <input
          type="email"
          placeholder="Digite seu e-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <label>Senha</label>
        <input
          type="password"
          placeholder="Crie uma senha"
          value={senha}
          onChange={(e) => setSenha(e.target.value)}
        />
        <button onClick={handleCadastro}>Cadastrar</button>
        <p>Já tem uma conta? <Link to="/">Faça login</Link></p>
      </div>
    </div>
  );
}
