import React, { useState, useEffect } from "react";
import {
  Container, Navbar, Nav, Row, Col, Card, Button, Form
} from "react-bootstrap";
import { FaMoon, FaSun, FaUser } from "react-icons/fa";
import { useNavigate, Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import { jwtDecode } from "jwt-decode";
import { useAuth } from "../context/AuthContext"; // ajuste o caminho conforme sua estrutura

export default function SearchCoursesPage() {
  const [darkMode, setDarkMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [savedIds, setSavedIds] = useState([]);
  const navigate = useNavigate();

  const { user, token, isAuthenticated } = useAuth();

  // Buscar cursos públicos da API
  useEffect(() => {
    fetch("https://localhost:7238/api/Curso")
      .then((res) => res.json())
      .then((data) => {
        setCourses(data);
        setFilteredCourses(data);
      })
      .catch((err) => console.error("Erro ao buscar cursos:", err));
  }, []);

  // Buscar cursos salvos do usuário autenticado
  useEffect(() => {
    if (isAuthenticated) {
      fetch("https://localhost:7238/api/CursosSalvos", {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => {
          const ids = data.map(curso => curso.id);
          setSavedIds(ids);
        })
        .catch(err => console.error("Erro ao buscar cursos salvos:", err));
    }
  }, [isAuthenticated, token]);

  const mainTheme = darkMode ? "bg-dark text-light" : "bg-light text-dark";
  const variant = darkMode ? "dark" : "light";
  const bgClass = darkMode ? "bg-secondary" : "bg-primary";
  const toggleDarkMode = () => setDarkMode(!darkMode);

  const handleSearch = () => {
    const filtered = courses.filter(course =>
      course.titulo.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCourses(filtered);
  };

  const handleSaveCourse = async (cursoId) => {
    if (!isAuthenticated) {
      alert("Você precisa estar logado para salvar cursos.");
      navigate("/login");
      return;
    }

    if (savedIds.includes(cursoId)) {
      alert("Este curso já está salvo.");
      return;
    }

    try {
      const res = await fetch("https://localhost:7238/api/CursosSalvos", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(cursoId)
      });

      if (res.ok) {
        setSavedIds([...savedIds, cursoId]);
        alert("Curso salvo com sucesso!");
      } else {
        const errText = await res.text();
        alert("Erro ao salvar curso: " + errText);
      }
    } catch (error) {
      console.error("Erro ao salvar curso:", error);
      alert("Erro inesperado ao salvar o curso.");
    }
  };

  return (
    <div className={mainTheme} style={{ minHeight: "100vh" }}>
      <Navbar expand="lg" variant={variant} className={`${bgClass} py-3`} fixed="top">
        <Container>
          <Navbar.Brand as={Link} to="/" className="fw-bold text-white">
            Curso<span className="text-warning">Finder</span>
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="navbar-nav" />
          <Navbar.Collapse id="navbar-nav">
            <Nav className="ms-auto align-items-center gap-3">
              <Nav.Link as={Link} to="/" className="text-white">
                Home
              </Nav.Link>
              <Nav.Link as={Link} to="/meuscursos" className="text-white">
                Meus Cursos Salvos
              </Nav.Link>

              {user ? (
                <span className="text-white fw-semibold">{user.nome || user.email}</span>
              ) : (
                <Button variant="light" size="sm" onClick={() => navigate("/login")}>
                  <FaUser className="me-2" />
                  Login
                </Button>
              )}

              <Button
                variant={darkMode ? "light" : "dark"}
                size="sm"
                onClick={toggleDarkMode}
                title={darkMode ? "Modo claro" : "Modo escuro"}
              >
                {darkMode ? <FaSun /> : <FaMoon />}
              </Button>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      <div style={{ paddingTop: '75px', paddingBottom: '150px' }}>
        <Container className="py-5">
          <h2 className="text-center mb-4">Busque o curso dos seus sonhos!</h2>

          <Form.Group controlId="search" className="mb-4">
            <Form.Control
              type="text"
              placeholder="Digite o nome do curso"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{ height: '60px', fontSize: '1.25rem' }}
              onKeyDown={(e) => e.key === 'Enter' && (e.preventDefault(), handleSearch())}
            />
            <div className="text-center mt-3">
              <Button variant="primary" size="lg" onClick={handleSearch}>
                Buscar
              </Button>
            </div>
          </Form.Group>

          <Row className="g-4">
            {filteredCourses.length > 0 ? (
              filteredCourses.map(course => (
                <Col key={course.id} md={4}>
                  <Card className="h-100 shadow">
                    <Card.Body className="d-flex flex-column justify-content-between">
                      <div>
                        <Card.Title>{course.titulo}</Card.Title>
                        <Card.Text>{course.descricao}</Card.Text>
                      </div>
                      <Button
                        variant={savedIds.includes(course.id) ? "success" : "outline-primary"}
                        onClick={() => handleSaveCourse(course.id)}
                      >
                        {savedIds.includes(course.id) ? "Salvo" : "Salvar"}
                      </Button>
                    </Card.Body>
                  </Card>
                </Col>
              ))
            ) : (
              <Col className="text-center">
                {searchTerm ? (
                  <p>Nenhum curso encontrado. Tente outro nome.</p>
                ) : (
                  <p>Digite um nome e clique em Buscar para ver os cursos.</p>
                )}
              </Col>
            )}
          </Row>
        </Container>
      </div>

      <footer className="bg-dark text-white py-4 mb-0" style={{ marginTop: "150px" }}>
        <Container className="text-center">
          <hr className="my-3" />
          <p className="mb-0">&copy; 2025 CursoFinder - Todos os direitos reservados</p>
        </Container>
      </footer>
    </div>
  );
}
