import { useState } from "react";

export default function RecuperacaoSenha() {
  const [email, setEmail] = useState("");
  const [mensagem, setMensagem] = useState("");

  const handleRecuperacao = async () => {
    try {
      const response = await fetch("https://localhost:7238/api/auth/forgot-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.text();
      setMensagem(data);
    } catch (error) {
      setMensagem("Insira um e-mail válido ou verifique se voce ");
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h2>Recuperar Senha</h2>
        <label>E-mail</label>
        <input
          type="email"
          placeholder="Digite seu e-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={handleRecuperacao}>Enviar</button>
        {mensagem && <p>{mensagem}</p>}
      </div>
    </div>
  );
}
