import { useState } from "react";
import { Container, Row, Col, Form, Button, Card } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

export default function SearchCoursesPage() {
    const [searchTerm, setSearchTerm] = useState("");
    const [category, setCategory] = useState("");
    const [courses, setCourses] = useState([
        { id: 1, title: "Curso de React", category: "Frontend", description: "Aprenda a construir aplicações com React." },
        { id: 2, title: "Curso de Node.js", category: "Backend", description: "Desenvolva aplicações backend com Node.js." },
        { id: 3, title: "Curso de Python", category: "Data Science", description: "Aprenda Python para análise de dados." },
        { id: 4, title: "Curso de JavaScript", category: "Frontend", description: "Domine JavaScript para o desenvolvimento web." },
    ]);
    const [filteredCourses, setFilteredCourses] = useState(courses);

    const handleSearch = () => {
        const filtered = courses.filter(course =>
            (course.title.toLowerCase().includes(searchTerm.toLowerCase())) &&
            (category ? course.category === category : true)
        );
        setFilteredCourses(filtered);
    };

    return (
        <Container className="py-5">
            <h2 className="text-center mb-4">Busque o curso dos seus sonhos!</h2>

            {/* Filtro de busca */}
            <Form className="mb-4">
                <Row className="align-items-center">
                    <Col md={6}>
                        <Form.Control
                            type="text"
                            placeholder="Digite o nome do curso"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                        />
                    </Col>

                    <Col md={4}>
                        <Form.Select
                            value={category}
                            onChange={(e) => setCategory(e.target.value)}
                        >
                            <option value="">Selecione a categoria</option>
                            <option value="Frontend">Frontend</option>
                            <option value="Backend">Backend</option>
                            <option value="Data Science">Data Science</option>
                        </Form.Select>
                    </Col>

                    <Col md={2}>
                        <Button variant="primary" onClick={handleSearch}>
                            Buscar
                        </Button>
                    </Col>
                </Row>
            </Form>

            {/* Exibir cursos */}
            <Row className="g-4">
                {filteredCourses.length > 0 ? (
                    filteredCourses.map(course => (
                        <Col key={course.id} md={4}>
                            <Card className="h-100 shadow">
                                <Card.Body>
                                    <Card.Title>{course.title}</Card.Title>
                                    <Card.Subtitle className="mb-2 text-muted">{course.category}</Card.Subtitle>
                                    <Card.Text>{course.description}</Card.Text>
                                    <Button variant="primary">Saiba mais</Button>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))
                ) : (
                    <Col className="text-center">
                        <p>Nenhum curso encontrado. Tente outro filtro ou pesquisa.</p>
                    </Col>
                )}
            </Row>
        </Container>
    );
}
